	TEST_F(HTTPTest, Signing)
	{
		const char header[] = "{\"alg\":\"RS256\",\"typ\":\"JWT\"}";
		const char bodyFormat[] =
			"{"
			"\"iss\":\"35978052136-02p54tlecq40fnrurg3ci9enarpueadm@developer.gserviceaccount.com\","
			"\"scope\":\"https://www.googleapis.com/auth/androidpublisher\","
			"\"aud\":\"https://www.googleapis.com/oauth2/v3/token\","
			"\"exp\":{0},"
			"\"iat\":{0}"
			"}";
		char body[1024];

		// 1hour later from now
		time_t timeToRequest = time(0) + 3;
		StrUtil::Format(body, bodyFormat, timeToRequest);

		DynamicArray<BYTE> result;

		Result hr = BR::Util::Base64URLEncode(countof(header) - 1, (const BYTE*)header, result);
		EXPECT_HRESULT_SUCCEEDED(hr);
		result.push_back('.');
		hr = Util::Base64URLEncode((INT)strlen(body), (const BYTE*)body, result);
		EXPECT_HRESULT_SUCCEEDED(hr);

		// load all open ssl algorithms
		OpenSSL_add_all_algorithms();

		// SHA256
		DynamicArray<BYTE> digest;
		hr = Util::SHA256Hash(result.GetSize(), result.data(), digest);
		EXPECT_HRESULT_SUCCEEDED(hr);


		// ssl signing
		// Load key file
		FILE *fp = fopen("decrypt.p12", "rb");
		
		PKCS12 * p12 = d2i_PKCS12_fp(fp, NULL);

		const char* password = "notasecret";
		auto sslResult = PKCS12_verify_mac(p12, password, (int)strlen(password));
		EXPECT_EQ(TRUE, sslResult);
		if (sslResult == FALSE)
		{
			// invalid password
			fclose(fp);
			return;
		}
		fclose(fp);

		EVP_PKEY* evp_pkey = nullptr;
		X509 *cert = nullptr;
		STACK_OF(X509) *ca = NULL;
		sslResult = PKCS12_parse(p12, password, &evp_pkey, &cert, &ca);
		EXPECT_EQ(TRUE, sslResult);
		if (sslResult == FALSE)
		{
			char errorMessage[512];
			auto error = ERR_get_error();
			ERR_error_string(error, errorMessage);
			return;
		}
		RSA* pkey = evp_pkey->pkey.rsa;
		PKCS12_free(p12);

		sk_X509_pop_free(ca, X509_free);
		X509_free(cert);


		//////////////////////////////////////////////////////////////////////
		// signing
		BYTE sign_buffer[512];
		UINT sign_len = (UINT)countof(sign_buffer);
		sslResult = RSA_sign(NID_sha256, digest.data(), (UINT)digest.GetSize(), sign_buffer, &sign_len, pkey);
		EXPECT_EQ(TRUE, sslResult);
		if (sslResult == FALSE)
		{
			return;
		}

		hr = result.push_back('.');
		EXPECT_HRESULT_SUCCEEDED(hr);

		hr = Util::Base64URLEncode(sign_len, sign_buffer, result);
		EXPECT_HRESULT_SUCCEEDED(hr);


		//////////////////////////////////////////////////////////////////////
		// send request
		CURLcode res = curl_global_init_mem(CURL_GLOBAL_ALL, CURL_malloc, CURL_free, CURL_realloc, CURL_strdup, CURL_calloc);
		EXPECT_EQ(CURLE_OK, res);
		if (res)
		{
			return;
		}

		const char* url = "https://www.googleapis.com/oauth2/v3/token";
		CURL *curl = nullptr;
		MemoryStream recvStream;

		char strPostFields[2048];
		result.push_back('\0'); // append null
		hr = StrUtil::Format(strPostFields, "{0}{1}", "grant_type=urn:ietf:params:oauth:grant-type:jwt-bearer&assertion=", (const char*)result.data());
		EXPECT_HRESULT_SUCCEEDED(hr);

		curl = curl_easy_init();
		EXPECT_NE(nullptr, curl);
		res = curl_easy_setopt(curl, CURLOPT_URL, url);
		EXPECT_EQ(CURLE_OK, res);
		Assert(res == 0);
		curl_easy_setopt(curl, CURLOPT_SSL_VERIFYPEER, false);

		res = curl_easy_setopt(curl, CURLOPT_POST, 1L);
		res = curl_easy_setopt(curl, CURLOPT_POSTFIELDS, strPostFields);
		res = curl_easy_setopt(curl, CURLOPT_POSTFIELDSIZE, strlen(strPostFields));

		struct curl_slist *headers = nullptr; // init to NULL is important 
		headers = curl_slist_append(headers, "Content-Type: application/x-www-form-urlencoded");
		//headers = curl_slist_append(headers, "charsets: utf-8");
		res = curl_easy_setopt(curl, CURLOPT_HTTPHEADER, headers);
		EXPECT_EQ(CURLE_OK, res);

		res = curl_easy_setopt(curl, CURLOPT_WRITEFUNCTION, writer);
		res = curl_easy_setopt(curl, CURLOPT_WRITEDATA, &recvStream);
		EXPECT_EQ(CURLE_OK, res);
		Assert(res == 0);

		res = curl_easy_perform(curl); /* ignores error */
		EXPECT_EQ(CURLE_OK, res);
		Assert(res == 0);

		char *ct = nullptr;
		res = curl_easy_getinfo(curl, CURLINFO_CONTENT_TYPE, &ct);
		EXPECT_EQ(CURLE_OK, res);
		Assert(res == 0);

		curl_easy_cleanup(curl);

		EVP_PKEY_free(evp_pkey);
	}


