					std::string value = plugins[index].asString();

				EXPECT_STREQ( "python", plugins[(UINT)0].asString().c_str() );
				EXPECT_STREQ( "c++", plugins[1].asString().c_str() );
				EXPECT_STREQ( "ruby", plugins[2].asString().c_str() );

